import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { BehaviorSubject } from 'rxjs';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent {
  isRespoLabo: boolean = true;
  isAgentControl: boolean = true;
  log: boolean | undefined;

  constructor(private Http:HttpClient){
  this.getBooleanValue().subscribe(value =>
    this.log=value)}
  getBooleanValue() {
    return this.booleanValue.asObservable();
  }

  setBooleanValue(newValue: boolean) {
    this.booleanValue.next(newValue);
    
  }
  private booleanValue = new BehaviorSubject<boolean>(false);
}
<nav class="my_navbar">
  <a class="my_navbar_logo" routerLink="/">
    <img ngSrc="assets/Logo.png" height="54" width="319">
  </a>
  <ul class="my_navbar_items">
    <li  >
      <a [routerLinkActive]="'active'" routerLink="/prelevements">{{log? 'Prélévemenets':''}}</a>
    </li>
    <li *ngIf="isRespoLabo">
      <a [routerLinkActive]="'active'" routerLink="resultatPrelevements">{{log? 'Prélévemenets':''}}</a>
    </li>
  </ul>
  <div class="my_navbar_avatar">
    <a href="#">
      <img *ngIf="isAgentControl || isRespoLabo" ngSrc="assets/avatar.png" height="50" width="50">
    </a>
  </div>
</nav>
