import { Component } from '@angular/core';
import {ActivatedRoute, Router} from "@angular/router";
import { RegisterRequest } from 'src/app/models/RegisterRequest.model';
import { AuthenticationService } from 'src/app/services/authentication.service';

@Component({
  selector: 'app-feedback',
  templateUrl: './feedback.component.html',
  styleUrls: ['./feedback.component.css']
})
export class FeedbackComponent {
  page: number=0;
  private RegisterRequest!: RegisterRequest;
  constructor(private router: Router,public authservice:AuthenticationService , private activatedRoute: ActivatedRoute) {
  }
  ngOnInit() {
    this.activatedRoute.paramMap.subscribe(params => {
      const pageString = params.get("page")
      if (typeof pageString === "string") {
        this.page = JSON.parse(pageString)
      }
    })
  }
  goToPrelevement() {
    this.router.navigate(['/prelevements', {page: this.page}])
  }
}
<div class="mt-5 col col-md-6 offset-3">
  <div class="feed ">
    <div class="icon">
      <svg xmlns="http://www.w3.org/2000/svg" width="50" height="50" fill="currentColor" class="bi bi-check-square" viewBox="0 0 16 16">
        <path d="M14 1a1 1 0 0 1 1 1v12a1 1 0 0 1-1 1H2a1 1 0 0 1-1-1V2a1 1 0 0 1 1-1h12zM2 0a2 2 0 0 0-2 2v12a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V2a2 2 0 0 0-2-2H2z"/>
        <path d="M10.97 4.97a.75.75 0 0 1 1.071 1.05l-3.992 4.99a.75.75 0 0 1-1.08.02L4.324 8.384a.75.75 0 1 1 1.06-1.06l2.094 2.093 3.473-4.425a.235.235 0 0 1 .02-.022z"/>
      </svg>
    </div>
    <div class="par">Bonjour Mr {{authservice.decodedToken.nom}}</div>
    <div class="par">Vos informations sont enregistrés avec succes</div>

    <button class="btn btn-primary" (click)="goToPrelevement()">Voir la liste des prélévements</button>

  </div>
</div>
