import { Component } from '@angular/core';
import {NgForm} from "@angular/forms";
import {Router} from "@angular/router";
import {HttpClient} from "@angular/common/http";
import {RegisterRequest} from "../../models/RegisterRequest.model";
import {AuthenticationService} from "../../services/authentication.service";
import { AuthResponse } from 'src/app/models/authResponse.model';
import { JwtHelperService } from '@auth0/angular-jwt';



@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  registerRequest: RegisterRequest = new RegisterRequest();
  helper=new JwtHelperService();
  profession!: string;
  mdp!: string;
  cmdp!:string;
  labo!:string;

  constructor(private http: HttpClient,private router:Router,
              private authService: AuthenticationService,private route:Router) {

  }


  onSubmit(f: NgForm) {
    //console.log(f.value.type);

    this.registerRequest.nom = f.value.nom
    this.registerRequest.prenom = f.value.prenom
    this.registerRequest.email = f.value.email
    this.registerRequest.mdp = f.value.mdp
    this.registerRequest.type = f.value.type
    this.registerRequest.labo = this.labo;
    console.log(this.registerRequest)

    this.authService.register(this.registerRequest).subscribe({
      next: (data: AuthResponse) => {
        console.log(data.token);
        localStorage.setItem("token", data.token);
        this.authService.setBooleanValue(true);
        this.authService.setDecodedToken(this.helper.decodeToken(data.token));
        //console.log(this.authService.decodedToken);
        this.route.navigate(['feedback']);
      },
      error: err => {
        console.log(err);
      }
    });


  }

}
function jwt_decode(token: string) {
  throw new Error('Function not implemented.');
}

<div class="top">
  <div class="header">Création du compte:</div>
  <div class="box">
    <form (ngSubmit)="onSubmit(f)" #f="ngForm" class="my_container">
      <div class="Nom">
        <label>Nom:</label>
        <input ngModel name="nom" required type="text">
      </div>

      <div class="Nom">
        <label>Prénom:</label>
        <input ngModel name="prenom" required type="text">
      </div>

      <div class="Nom">
        <label>Email:</label>
        <input ngModel name="email" required  type="email">
        <p class="error" *ngIf="Message" >{{Message}}</p>
      </div>

      <div class="Nom">
        <label>Profession:</label>
        <select  [(ngModel)]="profession" name="type" required >
          <option>AGENT_CONTROLE</option>
          <option >RESPO_LABO</option>
        </select>
      </div>

      <div class="Nom" *ngIf="f.value.type=='RESPO_LABO'">
        <label>Nom de Labo:</label>
        <select  [(ngModel)]="labo" name="lobo" required >
          <option>LOARC</option>
          <option >LRAR</option>
        </select>
      </div>
      <div *ngIf="f.value.type=='RESPO_LABO'"></div>

      <div class="Nom">
        <label>Mot de passe:</label>
        <input [(ngModel)]="mdp" name="mdp" required type="password">
      </div>

      <div class="Nom">
        <label>Confirmer mot de passe:</label>
        <input [(ngModel)]="cmdp" name="cmdp" required  type="password" >
      </div>


      <button type="submit" [disabled]="!f.valid || cmdp != mdp" class="btn btn-primary buttons">
        <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor" class="bi bi-save" viewBox="0 0 16 16">
          <path d="M2 1a1 1 0 0 0-1 1v12a1 1 0 0 0 1 1h12a1 1 0 0 0 1-1V2a1 1 0 0 0-1-1H9.5a1 1 0 0 0-1 1v7.293l2.646-2.647a.5.5 0 0 1 .708.708l-3.5 3.5a.5.5 0 0 1-.708 0l-3.5-3.5a.5.5 0 1 1 .708-.708L7.5 9.293V2a2 2 0 0 1 2-2H14a2 2 0 0 1 2 2v12a2 2 0 0 1-2 2H2a2 2 0 0 1-2-2V2a2 2 0 0 1 2-2h2.5a.5.5 0 0 1 0 1H2z"/>
        </svg>
        Enregistrer
      </button>

      <button type="button" class="btn btn-danger buttons">
        <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor" class="bi bi-x" viewBox="0 0 16 16">
          <path d="M4.646 4.646a.5.5 0 0 1 .708 0L8 7.293l2.646-2.647a.5.5 0 0 1 .708.708L8.707 8l2.647 2.646a.5.5 0 0 1-.708.708L8 8.707l-2.646 2.647a.5.5 0 0 1-.708-.708L7.293 8 4.646 5.354a.5.5 0 0 1 0-.708z"/>
        </svg>
        Annuler
      </button>

    </form>
  </div>
</div>
